import React, {Component,state} from 'react'
import {BrowserRouter, Route, Routes} from 'react-router-dom'
import './Loginform.css';

class Loginform extends Component {
  state ={
    username:'',
    password:'',
  }
  onSubmitSuccess=()=>{
    const {history} = this.props
    history.replace('/')
  }
 submitted= async event=>{
    event.preventDefault()
    const {username,password}=this.state
    const userdetails={username,password}
    const url="https://apis.ccbp.in/login"
    const options={
      method:"POST",
      body:JSON.stringify(userdetails),
    }
    const response=await fetch(url,options)
    const data=await response.json()
    console.log(response)
    if (response.ok === true) {
      this.onSubmitSuccess()
    }
    console.log(this.username,this.password)
  }
 onchangeuserinput=(e)=>{
    this.setState({username:e.target.value})
    console.log(this.username)
  }
  onchangepassinput=(e)=>{
    this.setState({password:e.target.value})
    console.log(this.password)
  }
  render(){
  return (
    <div>
      <h1 className="heading">E COMMERCE APPLICATION</h1>
        <div className="mainflex">
          <img className="image1" src="https://assets.ccbp.in/frontend/react-js/nxt-trendz-login-img.png" alt="websitelogin"></img>
          <div className="sub-flex">
            <img className="image2"src='https://assets.ccbp.in/frontend/react-js/nxt-trendz-logo-img.png ' alt="websitelogo"></img>
            <form onSubmit={this.submitted} id='FOOORM' name='userdetailsform'>
            <label className="labelnames">USERNAME</label>
            <input className='labelinput'type="text" value={this.username} onChange={this.onchangeuserinput}></input>
            <label className="labelnames">PASSWORD</label>
            <input className='labelinput'type="password"value={this.password} onChange={this.onchangepassinput}></input>
            <button  type='submit' className="loginbutton">LOGIN</button>
            </form>
          </div>
        </div>
    </div>
  );
}
}

export default Loginform;
